console.log('JavaScript code is running');

// Add event listeners to the switches
document.querySelectorAll('.switch input[type="checkbox"]').forEach(function (checkbox) {
    const slider = checkbox.nextElementSibling;

    checkbox.addEventListener('change', function () {
        slider.classList.toggle('checked');

        const overlayId = this.id.replace('Switch', 'Overlay');
        const overlay = document.getElementById(overlayId);

        if (overlay) {
            overlay.style.visibility = this.checked ? 'visible' : 'hidden';
        } else {
            console.error('Overlay element not found');
        }
    });
});

document.querySelector('.large-switch').addEventListener('click', function () {
    const checkbox = this.querySelector('input[type="checkbox"]');
    const slider = this.querySelector('.slider');

    checkbox.checked = !checkbox.checked;
    slider.classList.toggle('checked');

    // Trigger a custom event to indicate the state change
    const event = new Event('change');
    checkbox.dispatchEvent(event);
});

const masterSwitch = document.getElementById('covProvMaster');
const allSwitches = document.querySelectorAll('.switch input[type="checkbox"]');

masterSwitch.addEventListener('change', function () {
    const isChecked = masterSwitch.checked;

    allSwitches.forEach(function (checkbox) {
        if (checkbox.checked !== isChecked) {
            checkbox.checked = isChecked;
            checkbox.dispatchEvent(new Event('change'));
        }
    });
});

// Get the AST Global VSAT switch element
const astGVsatSwitch = document.querySelector('#astGVsat');

// Get the overlay element
const overlay = document.getElementById('ASTGVOverlay');

// Add event listener to the AST Global VSAT switch
astGVsatSwitch.addEventListener('change', function () {
    // Check if the overlay element exists
    if (overlay) {
        // Update the visibility of the overlay based on the switch state
        if (this.checked) {
            overlay.style.visibility = 'visible';
        } else {
            overlay.style.visibility = 'hidden';
        }
    } else {
        console.error('Overlay element not found');
    }
});
